package com.xsz.javabase;

public class ForDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<9;i++){
			System.out.println(i);
		}
		System.out.println("*****************************");
		
		for(int j=10;j>=0;j--){
			System.out.println(j);
		}
	}

}
