package com.xsz.javabase;

public class TypeDemo1 {
	

	public static void main(String[] args) {
		// 一共8个基本类型
		// TODO Auto-generated method stub
		//整数  最大值127，最小值-128
		byte a1=127;
		//整数  最大值是 32767（2^15 - 1）；，最小值是 -32768（-2^15）；
		short a2=1000;
		//整数  最小值是 -2,147,483,648（-2^31）最大值是 2,147,483,647（2^31 - 1）；
		int a3=50000;
		//整数 最小值是 -9,223,372,036,854,775,808（-2^63）； 最大值是 9,223,372,036,854,775,807（2^63 -1）；；
		long a4=888888888L;
		//浮点型
		float a5=8.3f;
		
		double a6=8.34d;
		//只有两个取值：true 和 false；
		boolean a7=true;
		//char类型，需要单引号
		char a8='a';
		char a9='中';

		
		
	}

}
